from math import comb

s = ""
totlength = len(s)
n = totlength-2
leftnum = 0
rightnum = 0
for i in range(0,n+1):
    kx=max(i,n-i)
    km=min(i,n-i)
    numtoadd = int(s[i])
    mult = comb(n,kx)%10 # can be improved by improving the computation of n choose k mod 10
    leftnum += numtoadd*mult
    leftnum = leftnum % 10
    rightnum += int(s[i+1])*mult
    rightnum = rightnum % 10
if leftnum == rightnum:
    print(True)
else:
    print(False)

# brute force implementation
totlength = len(s)
numsteps=0
arrstring = [0]*totlength
for i in range(0,totlength):
    arrstring[i]=int(s[i])
while totlength-numsteps>2:
    nextarr=arrstring
    for i in range(0,totlength-numsteps-1):
        nextarr[i]=(arrstring[i]+arrstring[i+1])%10
    arrstring=nextarr
    numsteps+=1
if arrstring[0]==arrstring[1]:
    print(True)
else:
    print(False)